Analytics.Script:
CREATE TEMPORARY TABLE DeviceTEMPData USING CarbonAnalytics OPTIONS(tableName "ORG_WSO2_IOT_DEVICES_TEMP");
CREATE TEMPORARY TABLE DeviceTEMPSummaryData USING CarbonAnalytics OPTIONS (tableName "DEVICE_TEMP_SUMMARY", schema "TEMP FLOAT, deviceType STRING -i, deviceId STRING -i, owner STRING -i, time LONG -i",primaryKeys "deviceType, deviceId, owner, time");
insert overwrite table DeviceTEMPSummaryData select TEMP, meta_deviceType as deviceType, meta_deviceId as deviceId, meta_owner as owner, cast(meta_time as BIGINT)as time from DeviceTEMPData group by TEMP, meta_deviceType, meta_deviceId, meta_owner, cast(meta_time as BIGINT);
